import{_ as e,c as t,al as s,o as n}from"./chunks/framework.BVh3rIuO.js";const u=JSON.parse('{"title":"Installation","description":"","frontmatter":{"github_branch":"main"},"headers":[],"relativePath":"en/cpp/guide/installation.md","filePath":"en/cpp/guide/installation.md"}'),l={name:"en/cpp/guide/installation.md"};function i(o,a,r,d,c,p){return n(),t("div",null,a[0]||(a[0]=[s('<h1 id="installation" tabindex="-1">Installation <a class="header-anchor" href="#installation" aria-label="Permalink to &quot;Installation {#installation}&quot;">​</a></h1><p>MAVSDK C++ is a library that can be installed and then used in your C++ code.</p><p>The latest release can be installed using prebuilt artefacts. If you need the latest state or want to debug the library itself, you need to <a href="./build.html">build it from source</a>.</p><h2 id="linux" tabindex="-1">Linux <a class="header-anchor" href="#linux" aria-label="Permalink to &quot;Linux&quot;">​</a></h2><ul><li><p><strong>Ubuntu:</strong> Download the <strong>.deb</strong> file for your system from <a href="https://github.com/mavlink/MAVSDK/releases" target="_blank" rel="noreferrer">MAVSDK releases</a> and install it using <code>dpkg</code>:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>sudo dpkg -i mavsdk_0.37.0_ubuntu20.04_amd64.deb</span></span></code></pre></div></li><li><p><strong>Fedora:</strong> Download the <strong>.rpm</strong> file for your system from <a href="https://github.com/mavlink/MAVSDK/releases" target="_blank" rel="noreferrer">MAVSDK releases</a> and install it using <code>rpm</code>:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>sudo rpm -U mavsdk-0.37.0-1.fc33-x86_64.rpm</span></span></code></pre></div></li><li><p><strong>Arch Linux:</strong> mavsdk is available from the <a href="https://aur.archlinux.org/" target="_blank" rel="noreferrer">AUR</a> and can be installed e.g. using <a href="https://aur.archlinux.org/packages/yay/" target="_blank" rel="noreferrer">yay</a>:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>yay -S mavsdk</span></span></code></pre></div></li></ul><h2 id="macos" tabindex="-1">macOS <a class="header-anchor" href="#macos" aria-label="Permalink to &quot;macOS&quot;">​</a></h2><p><strong>MacOS:</strong> Install <a href="https://brew.sh/" target="_blank" rel="noreferrer">Homebrew</a> and use it to install the library:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>brew install mavsdk</span></span></code></pre></div><h2 id="windows" tabindex="-1">Windows <a class="header-anchor" href="#windows" aria-label="Permalink to &quot;Windows&quot;">​</a></h2><p>For Windows you can download the <strong>mavsdk-windows-x64-release.zip</strong> file from <a href="https://github.com/mavlink/MAVSDK/releases" target="_blank" rel="noreferrer">MAVSDK releases</a> containing the headers and library and extract it locally (see <a href="./toolchain.html#sdk_local_install">information how to use a locally installed library</a>).</p><blockquote><p><strong>Note</strong> As the library is installed locally (in a local directory) you need to point cmake to that directory at configure time using <code>CMAKE_PREFIX_PATH</code> when building your application. i.e.: <code>cmake -Bbuild -DCMAKE_PREFIX_PATH=../mavsdk_extracted</code> -H.</p></blockquote>',11)]))}const g=e(l,[["render",i]]);export{u as __pageData,g as default};
